<?php (defined('BASEPATH')) OR exit('No direct script access allowed');/* load the MX_Loader class */require APPPATH."third_party/MX/Controller.php";class MY_Controller extends MX_Controller{	protected $_data;	public function __construct(){		parent::__construct();		$this->form_validation->CI =& $this;		$this->lang->load('form_validation','vietnamese');		$this->load->library('ckeditor', array('instanceName' => 'CKEDITOR1','basePath' => base_url()."ckeditor/", 'outPut' => true));		$this->load->helper('MY_convert_helper');        $this->load->model('mproduct');    }    public function    sendmail($config='',$from='' ,$to='' ,$sub='' ,$mess=''){        $config['wordwrap'] = FALSE;        $config['mailtype'] = 'html';        $config['smtp_timeout'] = 300;        $this->load->library('email',$config);        $this->email->set_newline("\r\n");        $this->email->from($from);        $this->email->to($to);        $this->email->subject($sub);        $this->email->message($mess);        if($this->email->send()){        }        else        {           // show_error($this->email->print_debugger());           // die();        }    }}